mapper.update:
update board_mst bm left outer join user_mst um on(um.user_code = bm.user_code) left outer join user_dtl ud on(ud.user_code = um.user_code) set um.user_name = #{user_name}, um.user_email = #{user_email}, ud.main_phone_number = #{main_phone_number}, company_code = #{company_code},
response_flag = #{response_flag},
board_title = #{board_title}, board_content = #{board_content} where board_code = #{board_code};
