mapper.select:
SELECT null as service_code, null as service_type_code, null as request_date, null as progress_status, null as product_name, null as service_type_name, null as company_name, (select COUNT(*) from repair_service_table where user_code = #{user_code} and non_member_flag = 0 and (progress_status = 2 OR progress_status = 0)
AND service_type_code = #{service_type_code}
)
+
(select COUNT(*) from recall_service_table where user_code = #{user_code} and non_member_flag = 0 and (progress_status = 2 OR progress_status = 0)
AND service_type_code = #{service_type_code}
) AS completed_total_count,
(select COUNT(*) from repair_service_table where user_code = #{user_code} and non_member_flag = 0 and progress_status = 1
AND service_type_code = #{service_type_code}
)
+
(select COUNT(*) from recall_service_table where user_code = #{user_code} and non_member_flag = 0 and progress_status = 1
AND service_type_code = #{service_type_code}
) AS incompleted_total_count,
(SELECT COUNT(*) from repair_service_table where user_code = #{user_code} and non_member_flag = 0
and progress_status = 1
and progress_status != 1
) +
(select COUNT(*) from recall_service_table where user_code = #{user_code} and non_member_flag = 0
and progress_status = 1
and progress_status != 1
) AS total_count
UNION
(SELECT rst.service_code, rst.service_type_code, rst.request_date, rst.progress_status,
pmd.model_number AS product_name,
stt.service_type_name,
pct.company_name,
0 AS completed_total_count, 0 AS incompleted_total_count, 0 as total_count FROM recall_service_table rst LEFT OUTER JOIN product_model_detail pmd ON(pmd.model_code = rst.model_code) LEFT OUTER JOIN service_type_table stt ON(stt.service_type_code = rst.service_type_code) LEFT OUTER JOIN winia_product_dtl wpd on(wpd.product_code = pmd.product_code) LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = wpd.product_category_code) LEFT OUTER JOIN product_company_table pct ON(pct.company_code = wpm.company_code) where user_code = #{user_code} and non_member_flag = 0 and
progress_status = 1
progress_status != 1
UNION
(
SELECT rst.service_code, rst.service_type_code, rst.request_date, rst.progress_status,
if(wpm.product_category_name != wpd.product_detail_name, concat(wpm.product_category_name, ' > ', wpd.product_detail_name), wpm.product_category_name) AS product_name,
stt.service_type_name,
pct.company_name,
0 AS completed_total_count, 0 AS incompleted_total_count, 0 as total_count FROM repair_service_table rst LEFT OUTER JOIN service_type_table stt ON(stt.service_type_code = rst.service_type_code) LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_code = rst.product_code) LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = wpd.product_category_code) LEFT OUTER JOIN product_company_table pct ON(pct.company_code = wpm.company_code) where user_code = #{user_code} and non_member_flag = 0
and progress_status = 1
and progress_status != 1
ORDER BY request_date DESC LIMIT #{page}, 10);
