mapper.select:
SELECT rst.service_code, rst.description, rst.user_name, rst.main_phone_number, rst.sub_phone_number, rst.email, rst.progress_status, rst.main_address, rst.detail_address, rst.postal_code, rst.trouble_code, rst.request_date,
wpm.product_category_name,
wpd.product_detail_name,
pct.company_code, pct.company_name,
pmd.model_number,
tst.trouble_symptom,
stt.service_type_name,
ert.reservation_date,
em.engineer_name,
pmg.product_category_name AS product_group_name,
rst_total_count.total_count FROM repair_service_table rst LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = rst.product_category_code) LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_code = rst.product_code) LEFT OUTER JOIN product_company_table pct ON(pct.company_code = wpm.company_code) LEFT OUTER JOIN product_model_detail pmd ON(pmd.model_code = rst.model_code) LEFT OUTER JOIN trouble_symptom_table tst ON(tst.trouble_code = rst.trouble_code) LEFT OUTER JOIN service_type_table stt on(stt.service_type_code = rst.service_type_code) LEFT OUTER JOIN engineer_reservation_table ert on(ert.service_code = rst.service_code) LEFT OUTER JOIN engineer_mst em ON(em.engineer_code = ert.engineer_code) LEFT OUTER JOIN product_main_group pmg ON(pmg.product_group = wpm.product_group) LEFT OUTER JOIN (select COUNT(*) AS total_count from repair_service_table where user_code = #{user_code}
AND service_type_code = #{service_type_code}
) rst_total_count ON(1 = 1) WHERE rst.user_code = #{user_code}
AND rst.service_type_code = #{service_type_code}
LIMIT #{page}, #{limit};
