mapper.select:
SELECT wpm.product_category_code, wpm.product_category_name, wpm.product_main_image, wpd.product_code, wpd.product_detail_name, wpd.product_detail_image FROM winia_product_mst wpm LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_category_code = wpm.product_category_code)
WHERE
wpm.product_group_code = #{code} AND wpm.company_code = #{company_code} AND main_group_flag != 1
wpm.product_category_code = #{code} AND wpm.company_code = #{company_code}
ORDER BY wpm.product_category_code;

SELECT pmc.product_category_code, pmc.product_category_number_info, pmc.product_category_number_info_detail,
wpm.product_category_name,
pmci.product_category_image_name FROM product_model_category pmc LEFT OUTER JOIN winia_product_mst wpm ON(pmc.product_category_code = wpm.product_category_code) LEFT OUTER JOIN product_model_category_image pmci ON(pmci.product_category_code = pmc.product_category_code);

SELECT model_code, model_number FROM product_model_detail
pmd LEFT OUTER JOIN winia_product_dtl wpd on (wpd.product_code = pmd.product_code)
WHERE
product_category_code = #{key_code}
product_code = #{key_code}
AND model_number LIKE concat("%", #{model_number}, "%");
