mapper.select:
SELECT '1' AS seq_order, spt.views, spt.solution_board_code,
st.solution_title, st.solution_content, st.create_date,
stt.solution_name,
wpd.product_detail_name,
wpm.product_category_name,
tct.total_count FROM solution_product_table spt LEFT OUTER JOIN solution_table st ON(st.solution_code = spt.solution_code) LEFT OUTER JOIN solution_type_table stt ON(stt.solution_type_code = st.solution_type_code) LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_code = spt.product_code) LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = wpd.product_category_code) LEFT OUTER JOIN (SELECT COUNT(*) AS total_count FROM solution_table st LEFT OUTER JOIN solution_product_table spt ON(spt.solution_code = st.solution_code) LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_code = spt.product_code) LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = wpd.product_category_code) WHERE wpm.company_code = #{company_code} AND st.solution_board_type = #{solution_board_type}
AND (st.solution_title like concat('%', #{keyword}, '%') OR st.solution_content like concat('%', #{keyword}, '%')) ) tct ON (1 = 1) WHERE wpm.company_code = #{company_code} AND st.solution_board_type = #{solution_board_type}
AND (st.solution_title like concat('%', #{keyword}, '%') OR st.solution_content like concat('%', #{keyword}, '%'))
AND st.create_date > #{limit_date}
UNION
SELECT '2' AS seq_order, spt.views, spt.solution_board_code,
st.solution_title, st.solution_content, st.create_date,
stt.solution_name,
wpd.product_detail_name,
wpm.product_category_name,
tct.total_count FROM solution_product_table spt LEFT OUTER JOIN solution_table st ON(st.solution_code = spt.solution_code) LEFT OUTER JOIN solution_type_table stt ON(stt.solution_type_code = st.solution_type_code) LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_code = spt.product_code) LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = wpd.product_category_code) LEFT OUTER JOIN (SELECT COUNT(*) AS total_count FROM solution_table st LEFT OUTER JOIN solution_product_table spt ON(spt.solution_code = st.solution_code) LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_code = spt.product_code) LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = wpd.product_category_code) WHERE wpm.company_code = #{company_code} AND st.solution_board_type = #{solution_board_type}
AND (st.solution_title like concat('%', #{keyword}, '%') OR st.solution_content like concat('%', #{keyword}, '%')) ) tct ON (1 = 1) WHERE wpm.company_code = #{company_code} AND st.solution_board_type = #{solution_board_type} and not st.create_date > #{limit_date}
AND (st.solution_title like concat('%', #{keyword}, '%') OR st.solution_content like concat('%', #{keyword}, '%'))
ORDER BY seq_order, views desc;
ORDER BY st.create_date desc;

SELECT '1' AS seq_order, spt.views, spt.solution_board_code,
st.solution_title, st.solution_content, st.create_date,
stt.solution_name,
wpd.product_detail_name,
wpm.product_category_name,
tct.total_count FROM solution_product_table spt LEFT OUTER JOIN solution_table st ON(st.solution_code = spt.solution_code) LEFT OUTER JOIN solution_type_table stt ON(stt.solution_type_code = st.solution_type_code) LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_code = spt.product_code) LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = wpd.product_category_code) LEFT OUTER JOIN (SELECT COUNT(*) AS total_count FROM solution_table st LEFT OUTER JOIN solution_product_table spt ON(spt.solution_code = st.solution_code) LEFT OUTER JOIN winia_product_dtl wpd ON(wpd.product_code = spt.product_code) LEFT OUTER JOIN winia_product_mst wpm ON(wpm.product_category_code = wpd.product_category_code) WHERE wpm.product_category_code = #{keyCode} AND st.solution_board_type = #{solution_board_type}
AND st.solution_type_code = #{solution_type_code}
AND (st.solution_title like concat('%', #{keyword}, '%') OR st.solution_content like concat('%', #{keyword}, '%'))
) tct ON (1 = 1) WHERE wpm.product_category_code = #{keyCode} AND st.solution_board_type = #{solution_board_type}
AND st.solution_type_code = #{solution_type_code}
AND (st.solution_title like concat('%', #{keyword}, '%') OR st.solution_content like concat('%', #{keyword}, '%'))
AND st.create_date > #{limit_date}
UNION
SELECT '2' AS seq_order, spt.views, spt.solution_board_code,
