AND st.solution_type_code = #{solution_type_code}
AND (st.solution_title like concat('%', #{keyword}, '%') OR st.solution_content like concat('%', #{keyword}, '%'))
) tct ON (1 = 1) WHERE wpm.product_category_code = #{keyCode} AND st.solution_board_type = #{solution_board_type} AND NOT st.create_date > #{limit_date}
AND st.solution_type_code = #{solution_type_code}
